var last, timeout;

module.exports = function (res, cb) {
  var values = [];
  res.replace(/\}[\n\r]?\{/g, '}|--|{') // }{
  .replace(/\}\][\n\r]?\[\{/g, '}]|--|[{') // }][{
  .replace(/\}[\n\r]?\[\{/g, '}|--|[{') // }[{
  .replace(/\}\][\n\r]?\{/g, '}]|--|{') // }]{
  .split('|--|').forEach(function (data) {
    if (last) data = last + data; // prepend the last chunk

    var result;

    try {
      result = JSON.parse(data);
    } catch (e) {
      last = data;
      clearTimeout(timeout); // restart timeout

      timeout = setTimeout(function () {
        return cb(new Error('Parse response timeout'));
      }, 15 * 1000);
      return;
    }

    clearTimeout(timeout);
    last = null;
    if (result) values.push(result);
  });
  cb(null, values);
};
