import React from 'react'
import { ButtonIcon, GU, IconMenu, useTheme } from '@aragon/ui'
import AccountModule from '../Account/AccountModule'
import ClockModule from '../ClockModule'
import ActivityButton from '../Activity/ActivityButton'
import GlobalPreferencesButton from '../GlobalPreferences/GlobalPreferencesButton'
import HeaderLogo from './HeaderLogo'

const Header = React.memo(function Header({
  compactMode,
  toggleMenuPanel,
  onOpenPreferences,
}) {
  const theme = useTheme()
  return (
    <header
      css={`
        position: relative;
        z-index: 3;
        height: ${10 * GU}px;
        background: ${theme.secsurface};
        border-bottom: 1px solid #25262b;
        box-shadow: rgb(0 0 0 / 28%) 0 2px 3px;
        display: flex;
        align-items: center;
        justify-content: space-between;
        padding: 0;
      `}
    >
      <div
        css={`
          display: flex;
          align-items: center;
          height: 100%;
          padding-left: ${3 * GU}px;
        `}
      >
        {compactMode ? (
          <ButtonIcon label="Open menu" onClick={toggleMenuPanel}>
            <IconMenu />
          </ButtonIcon>
        ) : (
          <HeaderLogo />
        )}
      </div>

      <div
        css={`
          flex-grow: 0;
          display: flex;
          height: 100%;
        `}
      >
        {!compactMode && <ClockModule />}
        <div
          css={`
            display: flex;
            height: 100%;
          `}
        >
          <AccountModule />
        </div>
        <div
          css={`
            display: flex;
            height: 100%;
            margin-left: ${2 * GU}px;
          `}
        >
          <GlobalPreferencesButton onOpen={onOpenPreferences} />
        </div>
        <div
          css={`
            display: flex;
            height: 100%;
          `}
        >
          <ActivityButton />
        </div>
      </div>
    </header>
  )
})

export default Header
